import { colorsTxtTypes } from "../style/constants";
import { Text as RNText, TextProps, TextStyle } from "react-native";
import React from "react";

export interface TextLayoutProps extends TextProps {
  textExtraSmall?: boolean;
  textSmall?: boolean;
  textMedium?: boolean;
  textLarge?: boolean;
  textExtraLarge?: boolean;

  bold?: boolean;

  // color?: typeof colorsTxtTypes[number];
}

export const Text = React.forwardRef<RNText, TextLayoutProps>(
  ({ style: passThroughStyle, ...props }, ref) => {
    // const style: TextStyle = {};
    return (
      <RNText
        ref={ref}
        // style={[style, passThroughStyle]}
        {...(props as TextLayoutProps)}
      />
    );
  }
);
